import React from "react";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/Logement.css";
import Carousel from "../components/Carousel";
import Accordion from "../components/Accordion";
import HeaderLogement from "../components/HeaderLogement";
import Proprietaire from "../components/Proprietaire";
import TagsNote from "../components/TagsNote";

function Logement({ houses }) {
  console.log(houses);
  const [owner, setOwner] = useState(null);
  const { id } = useParams();
  let navigate = useNavigate();

  useEffect(() => {
    const logement = houses.find((location) => location.id === id);
    console.log(logement);
    if (houses.lenght > 0) {
      if (logement) {
        setOwner(logement);
      } else {
        navigate("/404");
      }
    }
    // setOwner(Data);
  }, []);

  return (
    <main>
      <section className="container_proprietaires">
        {/* <Carousel /> */}
        <div className="proprietaire_description">
          <HeaderLogement logement={owner} />
          <Proprietaire logement={owner} />
        </div>
        <div className="proprietaire_tag-rating">
          <TagsNote logement={owner} />
        </div>
        <div className="container_accordion">
          <Accordion title="Description" logement={owner} />
          <Accordion title="Ã‰quipement" logement={owner} />
        </div>
      </section>
    </main>
  );
}
export default Logement;
