import React, { useState, useRef } from "react";
import image from "../styles/img/down-arrow.png";

// Accordeon
function Accordion(props) {
  const [isOpen, setIsOpen] = useState(false);
  const [Height, setHeight] = useState("0px");
  const [toggle, setToggle] = useState(false);

  // useRef = connaître la hauteur de l'élément du DOM
  const content = useRef(null);

  // Appel de la fonction pour ouvrir ou fermer
  function toggleAccordion() {
    // S'assurer qu'il y a bien un contenu avant
    if (content && content.current) {
      setIsOpen(isOpen === false ? true : false);
      setHeight(isOpen === true ? "0px" : content.current.scrollHeight + "px");
    }
  }

  return (
    <article
      id="accordions"
      className="accordions"
      onClick={() => setToggle(!toggle)}
    >
      <div className="p_container">
        <button onClick={toggleAccordion} className="about_button">
          <p id="about_title" className="about_title">
            {props.title}
          </p>
          <img
            className={toggle ? "arrow arrow_up" : "arrow arrow_down"}
            id="arrow"
            src={image}
            alt="drop-down"
          />
        </button>
        <div
          className="about_bg"
          ref={content}
          style={{ maxHeight: `${Height}` }}
        >
          <p className="p_content">{props.content}</p>
        </div>
      </div>
    </article>
  );
}
export default Accordion;
