import React from "react"
import "../styles/Logement.css";
// import { useEffect, useState } from "react";
// import axios from "axios";
import GetData from '../service/logementService';
import Carousel from "../components/Carousel";
import Accordion from "../components/Accordion";
import HeaderLogement from "../components/HeaderLogement";
import Proprietaire from "../components/Proprietaire";
// import Note from "../components/Note";

// URLSearchParams : définit des méthodes utilitaires pour travailler avec la chaîne de requête (les paramètres GET) d’une URL
const url = new URLSearchParams(window.location.search);
console.log(url);
// Retourne la première valeur associée au paramètre de recherche donné (ici l'id du photographe)
const idUrl = url.get('id');
console.log(idUrl);
// let allMedias, photographerFiltres;

function FicheLogement() {
  const resultFetch = GetData("/db/logements.json");
  // console.log(resultFetch);

  const locations = resultFetch.data;

  return (
    <main>
      {locations.map((location, index) => (
        <section key={index}>
          <Carousel/>
          <div>
            <HeaderLogement title={location.title} location={location.location} tags={location.tags} />
            <Proprietaire name={location.host.name} avatar={location.host.picture} />
            {/* <Note rating={parseInt(location.rating)} /> */}
          </div>
          <div className="w-full grid grid-cols-1 md:grid-cols-2 md:gap-8">
            <Accordion title="Description" content={location.description} />
            <Accordion title="Équipement" content={location.equipments} />
          </div>
      </section> 
      ))}
    </main>
  )
}

