import { useState } from "react";
import "../../src/styles/Carrousel.css";
import BtnSlider from "./BtnSlider";
// import leftArrow from "../styles/img/right-arrow.png";
// import rightArrow from "../styles/img/left-arrow.png";

function Carrousel(props) {
  const picturesArray = props.logement.pictures;

  const [slideIndex, setSlideIndex] = useState(1);

  const nextSlide = () => {};

  const prevSlide = () => {};

  return (
    <div className="carrousel_container">
      {picturesArray.map((picture, index) => (
        <img
          className="img_carrousel"
          src={process.env.PUBLIC_URL + picture}
          key={index}
          alt="pictures_carrousel"
        />
      ))}
      <BtnSlider moveslide={nextSlide} />
      <BtnSlider moveslide={prevSlide} />
    </div>
  );
}
export default Carrousel;
