import { useState } from "react";
import "../../src/styles/Carrousel.css";
import BtnSlider from "./BtnSlider";

function Carrousel(props) {
  const picturesArray = props.logement.pictures;
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () =>
    currentIndex === picturesArray.length - 1
      ? setCurrentIndex(0)
      : setCurrentIndex(currentIndex + 1);
  const prevSlide = () =>
    currentIndex === 0
      ? setCurrentIndex(picturesArray.length - 1)
      : setCurrentIndex(currentIndex - 1);

  return (
    <div className="container-slider">
      <div className="slide">
        <img src={picturesArray[currentIndex]} alt="pictures_carrousel" />
      </div>
      <BtnSlider moveSlide={prevSlide} direction="next" />
      <BtnSlider moveSlide={nextSlide} direction="prev" />
    </div>
  );
}
export default Carrousel;
