    const equitmentList = equipments.map((equip) => <li key={equip}>{equip}</li>);

    return (
      <div>
        {/* <CarouselGallery imageArray={pictures} description={description} /> */}
        <HeaderLogement title={title} location={location}/>
        <Proprietaire name={host.name} picture={host.picture}/>
        <TagsNote tagList={tags} rating={rating}/>
        <div>
          <Accordion
            title="Description"
            paragraphe={description}
          />
          <Accordion
            title="Equipments"
            paragraphe={equitmentList}
          />
        </div>
      </div>
    );
  }
export default Logement;
