import { useState } from "react";
import "../../src/styles/Carrousel.css";
import BtnSlider from "./BtnSlider";

function Carrousel(props) {
  const picturesArray = props.logement.pictures;
  console.log(picturesArray);

  const [slideIndex, setSlideIndex] = useState(0);

  const nextSlide = () => {
    if (slideIndex !== picturesArray.length) {
      setSlideIndex(slideIndex + 1);
    } else if (slideIndex === picturesArray.length) {
      setSlideIndex(1);
    }
  };

  const prevSlide = () => {
    if (slideIndex !== 1) {
      setSlideIndex(slideIndex - 1);
    } else if (slideIndex === 1) {
      setSlideIndex(picturesArray.length);
    }
  };

  return (
    <div className="container-slider">
      {picturesArray.map((picture, index) => (
        <div
          className={slideIndex === index + 1 ? "slide active-anim" : "slide"}
          key={index}
        >
          <img src={picture} alt="pictures_carrousel" />
        </div>
      ))}
      <BtnSlider moveSlide={prevSlide} direction="next" />
      <BtnSlider moveSlide={nextSlide} direction="prev" />
    </div>
  );
}
export default Carrousel;
