import { useState } from "react";
import "../../src/styles/Carrousel.css";
import leftArrow from "../styles/img/right-arrow.png";
import rightArrow from "../styles/img/left-arrow.png";

function Carrousel(props) {
  const picturesArray = props.logement.pictures;

  const [currentIndex, setCurrentIndex] = useState(0);
  const displayArrow = picturesArray.length > 1;

  const next = () => {
    if (currentIndex < picturesArray.length - 1) {
      setCurrentIndex((prevState) => prevState + 1);
    } else {
      setCurrentIndex(0);
    }
  };

  const previous = () => {
    if (currentIndex > 0) {
      setCurrentIndex((prevState) => prevState - 1);
    } else {
      setCurrentIndex(picturesArray.length - 1);
    }
  };

  return (
    <div className="carrousel_container">
      {picturesArray.map((picture, index) => (
        <img
          className="img_carrousel"
          src={picture}
          key={index}
          alt="pictures_carrousel"
        />
      ))}
      {displayArrow && (
        <>
          <div>
            <img src={leftArrow} onClick={previous} alt="previous" />
            <img src={rightArrow} onClick={next} alt="next" />
          </div>
          <div>
            <span>{`${currentIndex + 1}/${picturesArray.length}`}</span>
          </div>
        </>
      )}
    </div>
  );
}
export default Carrousel;
