// import { useState } from 'react'
import "../../src/styles/Carrousel.css";
// import leftArrow from "../styles/img/right-arrow.png";
import rightArrow from "../styles/img/right-arrow.png";

function BtnSlider({ direction, moveSlide }) {
  return (
    <button
      className={direction === "next" ? "btn_slider next" : "btn_slider prev"}
      onClick={moveSlide}
    >
      <img src={rightArrow} alt="next" />
    </button>
  );
}
export default BtnSlider;
